package newjava;

public class Withoutdivmulop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int c = 18;
		while (c > 0) {
			c = c - 3;
		}
		if (c == 0)
			System.out.println("MULTIPLE OF 3");
		else
			System.out.println("NOT MULTIPLE OF 3");

	}

}


















